import { createRouter, createWebHistory } from 'vue-router'
import Dashboard from '../views/Dashboard.vue' // 直接导入，如果不大可以不懒加载

const routes = [
  {
    path: '/',
    name: 'dashboard',
    component: Dashboard
  },
  {
    path: '/monitoring',
    name: 'monitoring',
    // 懒加载组件
    component: () => import(/* webpackChunkName: "monitoring" */ '../views/Monitoring.vue')
  },
  {
    path: '/owner-management',
    name: 'ownerManagement',
    component: () => import(/* webpackChunkName: "owner-management" */ '../views/OwnerManagement.vue')
  },
  {
    path: '/work-order',
    name: 'workOrder',
    component: () => import(/* webpackChunkName: "work-order" */ '../views/WorkOrder.vue')
  },
  // 将原来指向 placeholder 的路由也设置好
  {
    path: '/visitor', // 访客管理
    name: 'visitor',
    component: () => import(/* webpackChunkName: "placeholder" */ '../views/Placeholder.vue')
  },
  {
    path: '/appointment', // 预约管理
    name: 'appointment',
    component: () => import(/* webpackChunkName: "placeholder" */ '../views/Placeholder.vue')
  },
  {
    path: '/parking', // 车位管理
    name: 'parking',
    component: () => import(/* webpackChunkName: "placeholder" */ '../views/Placeholder.vue')
  },
  // 可以添加一个 404 页面或者重定向到首页
  // { path: '/:pathMatch(.*)*', name: 'NotFound', redirect: '/' }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL || '/'), // 使用 process.env.BASE_URL 或默认为 '/'
  routes
})

export default router
